import React from 'react';
import './Cart.css'
const Cart = (props) => {

    const { cart } = props;
    let total = 0;
    for (const product of cart) {
        total += product.price
    }

    return (
        <div className="internal-cart">
            <h2>Total Order summary</h2>
            <h3>Order:{cart.length} </h3>
            <h3>Total Price:${total} </h3>
        </div>
    );
};

export default Cart;
